import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import {SpotifyService} from "../services/spotify.service";
import {DomSanitizer} from "@angular/platform-browser";


@Component({
  selector: 'app-video-player',
  templateUrl: './video-player.component.html',
  styleUrls: ['./video-player.component.css']
})
export class VideoPlayerComponent {
  song: any;
  videoUrl: SafeResourceUrl = '';

  constructor(
    private route: ActivatedRoute,
    private spotifyService: SpotifyService,
    private sanitizer: DomSanitizer
  ) { }

  ngOnInit(): void {
    // @ts-ignore
    const id = +this.route.snapshot.paramMap.get('id');
    this.spotifyService.getSong(id).subscribe((data: any) => {
      this.song = data;
      if (this.song && this.song.youtube) {
        this.videoUrl = this.sanitizer.bypassSecurityTrustResourceUrl(this.song.youtube.replace("watch?v=", "embed/"));
      }
    });
  }
}
<div class="container mx-auto p-4">
  <button class="bg-blue-600 text-white py-2 px-4 rounded mb-4" routerLink="/">Retour</button>
  <div class="bg-black rounded p-4">
    <iframe width="100%" height="700" [src]="videoUrl" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>
  </div>
</div>
