import { Component } from '@angular/core';

@Component({
  selector: 'app-choose-playlist',
  templateUrl: './choose-playlist.component.html',
  styleUrls: ['./choose-playlist.component.css']
})
export class ChoosePlaylistComponent {

}
<div class="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800">
  <div class="container mx-auto p-4">
    <h2 class="text-3xl font-semibold text-green-500 mb-4">Choisir une playlist</h2>
    <div class="grid grid-cols-2 gap-4">
      <!-- Remplacer les valeurs d'exemple par les données réelles des playlists -->
      <!-- Les playlists seront affichées ici avec un bouton de sélection pour chaque playlist -->
    </div>
  </div>
</div>
